package elmhurst;

public class Whileloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int j=0;
		
		while(j<=4) {
			System.out.println(j);
			j++;
		}
		
		while(j<=10) {
			System.out.println(j+"ans");
			j++;
		}
		int i=10;
		while(i>=0) {
			System.out.println(i);
			i--;
		}
		
		int q=0;
		while(q<=10) {
			System.out.println(q);
			q= q+2;
		}
		

	}

}
